@Component({
  selector: 'blog-home',
  templateUrl: './blog-home.component.html',
  styleUrls: ['./blog-home.component.css']
})
export class BlogHomeComponent implements OnInit {

  filterText?: string;

  constructor() { }

  ngOnInit() {
  }

  getName($event: string) {
    this.filterText = $event;
  }
}
